--DROP TABLE facility;
CREATE TABLE facility ( id int NOT NULL AUTO_INCREMENT, name varchar(50) NOT NULL, station int NOT NULL, visn int, server varchar(45), createdAt datetime, updatedAt datetime, PRIMARY KEY (id) ) ENGINE=InnoDB DEFAULT CHARSET=utf8;
--DROP TABLE facility_message;
CREATE TABLE facility_message ( id int NOT NULL AUTO_INCREMENT, facility_id int NOT NULL, active tinyint(1) DEFAULT '1' NOT NULL, message_order int NOT NULL, message_text varchar(200) NOT NULL, message_headline varchar(50) NOT NULL, createdAt datetime, updatedAt datetime, PRIMARY KEY (id), CONSTRAINT facility_message_fkey FOREIGN KEY (facility_id) REFERENCES facility (id) ON DELETE NO ACTION ON UPDATE NO ACTION, INDEX facility_message_idx_facility_id (facility_id) ) ENGINE=InnoDB DEFAULT CHARSET=utf8;
--DROP TABLE panel_type;
CREATE TABLE panel_type ( id int NOT NULL AUTO_INCREMENT, title varchar(45) NOT NULL, directive varchar(45) NOT NULL, scope_variable varchar(45) NOT NULL, minSizeX int, minSizeY int, createdAt datetime, updatedAt datetime, PRIMARY KEY (id), INDEX panel_type_idx_name (title) ) ENGINE=InnoDB DEFAULT CHARSET=utf8;
--DROP TABLE panel;
CREATE TABLE panel ( id int NOT NULL AUTO_INCREMENT, name varchar(45) NOT NULL, panel_type_id int NOT NULL, location_id int, description varchar(100), sizeX int, sizeY varchar(45), createdAt datetime, updatedAt datetime, PRIMARY KEY (id), CONSTRAINT panel_panel_type_fkey FOREIGN KEY (panel_type_id) REFERENCES panel_type (id) ON DELETE NO ACTION ON UPDATE NO ACTION, INDEX panel_panel_type_fkey_idx (panel_type_id), INDEX panel_idx_name (name), INDEX panel_idx_location_id (location_id) ) ENGINE=InnoDB DEFAULT CHARSET=utf8;
--DROP TABLE panel_setting;
CREATE TABLE panel_setting ( id int NOT NULL AUTO_INCREMENT, panel_type_id int NOT NULL, setting_type int NOT NULL, setting_name varchar(45) NOT NULL, setting_value varchar(45) NOT NULL, createdAt datetime, updatedAt datetime, PRIMARY KEY (id), CONSTRAINT panel_setting_panel_type_fkey FOREIGN KEY (panel_type_id) REFERENCES panel_type (id) ON DELETE NO ACTION ON UPDATE NO ACTION, INDEX panel_setting_panel_type_fkey_idx (panel_type_id) ) ENGINE=InnoDB DEFAULT CHARSET=utf8;
--DROP TABLE panel_detail;
CREATE TABLE panel_detail ( id int NOT NULL AUTO_INCREMENT, panel_id int NOT NULL, panel_setting_id int NOT NULL, detail_value varchar(45), createdAt datetime, updatedAt datetime, PRIMARY KEY (id), CONSTRAINT panel_detail_panel_id_fkey FOREIGN KEY (panel_id) REFERENCES panel (id) ON DELETE NO ACTION ON UPDATE NO ACTION, CONSTRAINT panel_detail_panel_setting_id_fkey FOREIGN KEY (panel_setting_id) REFERENCES panel_setting (id) ON DELETE NO ACTION ON UPDATE NO ACTION, INDEX panel_detail_panel_id_fkey_idx (panel_id), INDEX panel_detail_panel_setting_id_fkey_idx (panel_setting_id) ) ENGINE=InnoDB DEFAULT CHARSET=utf8;
--DROP TABLE template;
CREATE TABLE template ( id int NOT NULL AUTO_INCREMENT, template_name varchar(45) NOT NULL, template_description varchar(100), location_id int, active tinyint(1), template_owner int, createdAt datetime, updatedAt datetime, PRIMARY KEY (id), INDEX template_idx_template_name (template_name), INDEX template_idx_location_id (location_id) ) ENGINE=InnoDB DEFAULT CHARSET=utf8;
--DROP TABLE template_layout;
CREATE TABLE template_layout ( id int NOT NULL AUTO_INCREMENT, template_id int NOT NULL, panel_id int NOT NULL, panel_order int NOT NULL, optional tinyint(1), createdAt datetime, updatedAt datetime, PRIMARY KEY (id), CONSTRAINT template_layout_panel_id_fkey FOREIGN KEY (panel_id) REFERENCES panel (id) ON DELETE NO ACTION ON UPDATE NO ACTION, CONSTRAINT template_layout_template_id_fkey FOREIGN KEY (template_id) REFERENCES template (id) ON DELETE NO ACTION ON UPDATE NO ACTION, INDEX template_layout_template_id_fkey_idx (template_id), INDEX template_layout_panel_id_fkey_idx (panel_id) ) ENGINE=InnoDB DEFAULT CHARSET=utf8;
